package com.zeroone_creative.basicapplication.controller.provider;import android.graphics.Bitmap;import android.support.v4.util.LruCache;public class BitmapCache extends LruCache<String, Bitmap> implements CustomImageLoader.ImageCache {    public static int getDefaultLruCacheSize() {        final int maxMemory = (int) (Runtime.getRuntime().maxMemory() / 1024);        final int cacheSize = maxMemory / 8;        return cacheSize;    }    public BitmapCache() {        this(getDefaultLruCacheSize());    }    public BitmapCache(int memorySize) {        super(memorySize);    }    /**     * {@inheritDoc}     */    @Override    protected int sizeOf(String key, Bitmap value) {        return value.getRowBytes() * value.getHeight() / 1024;    }    /**     * {@inheritDoc}     */    @Override    public Bitmap getBitmap(String url) {        return get(url);    }    /**     * {@inheritDoc}     */    @Override    public void putBitmap(String url, Bitmap bitmap) {        put(url, bitmap);    }    /**     * {@inheritDoc}     */    @Override    protected void entryRemoved(boolean evicted, String key, Bitmap oldBitmap, Bitmap newBitmap) {        if (oldBitmap.isRecycled() == false && evicted) {            oldBitmap = null;        }    }}